// D√©claration de l&#39;enum√©ration repr√©sentant les √©tats enum state_e { ST_CLOSED, ST_OPENED, ST_CLOSING, ST_OPENING }; enum state_e state = ST_CLOSED; // D√©claration et initialisation de la variable contenant l&#39;√©tat void main() { while (1) { switch(state) { case ST_CLOSED: // Condition de changement d&#39;√©tat if (BTN_OPEN == BTN_ON) { state = ST_OPENING; } break; case ST_OPENED: // Comportement si le syst√®me est dans cet √©tat if (BTN_OPEN == BTN_ON) { reset_timer_opened(); } // Condition de changement d&#39;√©tat if (timer_opened &gt;= OPENED_DURATION) { state = ST_CLOSING; } break; case ST_CLOSING: // Comportement si le syst√®me est dans cet √©tat move_door(DIRECTION_CLOSE); // Condition de changement d&#39;√©tat if (SENSOR_SECURITY == PERSON_DETECTED || BTN_OPEN == BTN_ON) { state = ST_OPENING; } else if (SENSOR_CLOSED == SENSOR_ON) { state = ST_CLOSED; } break; case ST_OPENING: // Comportement si le syst√®me est dans cet √©tat move_door(DIRECTION_OPEN); // Condition de changement d&#39;√©tat if (SENSOR_OPENED == SENSOR_ON) { reset_timer_opened(); state = ST_OPENED; } break; } } }
